// Define arrays of different types of content
const cornyJokes = [
  "Why don't scientists trust atoms? Because they make up everything!",
  "I told my wife she was drawing her eyebrows too high. She looked surprised.",
  "What do you call a fake noodle? An impasta!",
  "Why did the scarecrow win an award? Because he was outstanding in his field!",
  "Why don't eggs tell jokes? They'd crack each other up.",
  "What's the best time to go to the dentist? Tooth-hurty!",
  "How do you organize a space party? You planet!",
  "Why don't skeletons fight each other? They don't have the guts.",
  "What do you call a parade of rabbits hopping backwards? A receding hare-line.",
  "What's brown and sticky? A stick!"
];

const puns = [
  "I'm on a seafood diet. Every time I see food, I eat it.",
  "I wondered why the baseball was getting bigger. Then it hit me.",
  "I used to be a baker, but I couldn't make enough dough.",
  "When life gives you melons, you're dyslexic.",
  "It's hard to explain puns to kleptomaniacs because they always take things literally.",
  "Time flies like an arrow. Fruit flies like a banana.",
  "A bicycle can't stand on its own because it's two-tired.",
  "The future, the present, and the past walked into a bar. It was tense.",
  "I wasn't originally going to get a brain transplant, but then I changed my mind.",
  "A backward poet writes inverse."
];

const inspirationalQuotes = [
  "The only way to do great work is to love what you do. - Steve Jobs",
  "Believe you can and you're halfway there. - Theodore Roosevelt",
  "It does not matter how slowly you go as long as you do not stop. - Confucius",
  "You are never too old to set another goal or to dream a new dream. - C.S. Lewis",
  "The future belongs to those who believe in the beauty of their dreams. - Eleanor Roosevelt",
  "Everything you've ever wanted is on the other side of fear. - George Addair",
  "Success is not final, failure is not fatal: It is the courage to continue that counts. - Winston Churchill",
  "The only limit to our realization of tomorrow is our doubts of today. - Franklin D. Roosevelt",
  "In the middle of difficulty lies opportunity. - Albert Einstein",
  "Don't watch the clock; do what it does. Keep going. - Sam Levenson"
];

const funFacts = [
  "Honey never spoils. Archaeologists have found pots of honey in ancient Egyptian tombs that are over 3,000 years old and still perfectly good to eat.",
  "Octopuses have three hearts and blue blood.",
  "The shortest war in history was between Britain and Zanzibar on August 27, 1896. Zanzibar surrendered after 38 minutes.",
  "A group of flamingos is called a 'flamboyance'.",
  "The average person will spend six months of their life waiting at red lights.",
  "A day on Venus is longer than a year on Venus. It takes 243 Earth days to rotate once on its axis, but only 225 Earth days to go around the Sun.",
  "The fingerprints of koalas are so similar to humans that they have on occasion been confused at crime scenes.",
  "Cows have best friends and get stressed when they're separated.",
  "A cockroach can live for several weeks without its head.",
  "The Hawaiian alphabet only has 12 letters."
];

// Choose content type randomly
const contentTypes = ['joke', 'pun', 'quote', 'fact'];
const randomType = contentTypes[Math.floor(Math.random() * contentTypes.length)];

// Choose random content based on the selected type
let content, contentTypeName;
switch(randomType) {
  case 'joke':
    content = cornyJokes[Math.floor(Math.random() * cornyJokes.length)];
    contentTypeName = "Corny Joke";
    break;
  case 'pun':
    content = puns[Math.floor(Math.random() * puns.length)];
    contentTypeName = "Pun";
    break;
  case 'quote':
    content = inspirationalQuotes[Math.floor(Math.random() * inspirationalQuotes.length)];
    contentTypeName = "Inspirational Quote";
    break;
  case 'fact':
    content = funFacts[Math.floor(Math.random() * funFacts.length)];
    contentTypeName = "Fun Fact";
    break;
}

// Create the response message
const response = `üéÅ *${contentTypeName}* üéÅ\n\n${content}`;

// Return the data
return [
  {
    json: {
      chatId: $input.item.json.message.chat.id,
      response: response
    }
  }
];
